        (title LIKE '%${key}%' or sub_title  LIKE '%${key}%')


        and saleable=#{saleable}

SELECT * FROM tb_spu


        (title LIKE '%${key}%' or sub_title LIKE '%${key}%')


        and saleable=#{saleable}


